INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (1,1,"Chó","Chó là con vật thân thiết gắn bó thủy chung với người chủ nói riêng và con người nói chung, những đặc tính của chó được tôn vinh, thậm chí có nơi chó được thờ cúng tại các đền thờ, miếu mạo");
INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (2,1,"Mèo"," Mèo là động vật có vú nhỏ và ăn thịt, mèo được nuôi để săn bắt các con vật gây hại như chuột, các loài gậm nhấm, côn trùng ( gián)...");
INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (3,1,"Thỏ"," Thỏ là động vật có vú nhỏ, thích gậm nhấm, được xếp vào họ Leporidae thuộc bộ Lagomorpha, sinh sống ở nhiều nơi trên thế giới. Thỏ được phân thành bảy loại, điển hình như thỏ rừng châu Âu, thỏ đuôi bông, thỏ Amami ...");
INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (4,1,"Chuột","Dù nhiều con trông có vẻ bẩn và không bắt mắt, thực chất chuột lại làm thú nuôi rất tốt. Chuột thông minh, sạch, dễ dạy dễ bắt nạt và có thể mang theo người ( bỏ túi luôn). Dĩ nhiên, không đơn giản như việc cứ đặt bẫy một con rồi nuôi nó. Ta mua từ cửa hàng thú nuôi để đảm bảo an toàn và mua được chuột khỏe mạnh");
INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (5,1,"Chim","Có rất nhiều loại chim. Nếu không muốn tốn kém, bạn có thể nuôi vẹt hoặc sáo và dạy chúng nói bi bô cả ngày. Ngoài ra còn có nhiều loại thú cưng đắt tiền và khó chơi như đại bàng, bạch yến … Nếu từ niềm đam mê thì hẳn bạn cũng tốn một khoản tiền kha khá để đầu tư cho chúng đấy.");
INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (6,1,"Cá"," Bể cá sặc sỡ cũng thu hút nhiều sự chú ý khi bước vào phòng. Có thể bạn cho rằng nuôi loài này chỉ để trang trí, chứ chúng không có tình cảm. Nhưng cá cũng được coi là một thú cưng trong nhà.");
