INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (10,2,"Rắn","Rắn là  loại vật nuôi chưa phổ biến ở nước ta vì nhận thức của chúng ta về rắn (loại nào độc, loại nào không) vẫn chưa rõ ràng. Rắn bị xem là mồi nhậu nhiều hơn là một con vật kiểng. Nhắc đến nuôi rắn nhiều người nghĩ mình bị hâm vì cho rằng rắn nào cũng có độc.");
INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (11,2,"Sóc bay","Sóc đất vốn là loài ăn tạp, ngoài việc ăn các loại quả, hạt, bọn này có khả năng kháng độc rất tốt, do đó chúng không hề sợ rắn độc. Sóc đất có lông màu nâu, bên hông có sọc, lông bụng màu trắng, kem, vàng kem, vàng nhạt hoặc vàng cam(rất hiếm), lông đuôi xù.");
INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (12,2,"Nhện","Nhện Tarantula dễ nuôi, môi trường sống đơn giản, chỉ cần một cái lồng thoáng khí, đất, một chỗ trú ẩn là được. Theo các dân chơi, tuổi thọ nhện rất dài. Nhện đực có thể sống tới 5 năm. Nhện cái tùy loại, thường từ 5 tới 20 năm.");
