INSERT INTO loai_dong_vat(id, nhom_dong_vat_id, ten_loai, mo_ta_dac_diem) VALUES (15,2,"Rồng đất","Xuất xứ từ vùng Caribe Nam Mỹ. Trên lưng thường có sừng (gai) nhìn hầm hố như một chú rồng trong chuyện cổ tích. Nhưng loài bò sát này hiền như bụt và chỉ ăn rau củ quả: khoai tây, cà rốt, cà chua…Loài rồng đất này có tuổi thọ rất cao, khoảng từ 15 tới 20 năm.");
